def legendre_symbol(a, p):
    """Calcula el símbolo de Legendre de 'a' módulo 'p'."""
    ls = pow(a, (p - 1) // 2, p)
    if ls != p - 1:
        return ls
    else:
        return -1

def tonelli_shanks(a, p):
    """Calcula la raíz cuadrada de 'a' módulo el primo 'p'.

    Devuelve dos valores 'x' que son las soluciones de la ecuación
    x^2 ≡ a (mod p). Si no hay solución, devuelve None.
    """
    if legendre_symbol(a, p) != 1:
        return None, None

    # Factorizamos p - 1 como (2^s * q)
    q = p - 1
    s = 0
    while q % 2 == 0:
        q //= 2
        s += 1

    # Encuentra un residuo cuadrático no trivial 'z'
    z = 2
    while legendre_symbol(z, p) != -1:
        z += 1

    # Inicialización
    m = s
    c = pow(z, q, p)
    t = pow(a, q, p)
    r = pow(a, (q + 1) // 2, p)

    # Bucle principal
    while t != 1:
        # Encuentra el menor 'i' tal que t^(2^i) ≡ 1 (mod p)
        i = 1
        for i in range(1, m):
            if pow(t, 2**i, p) == 1:
                break

        # Actualiza valores
        b = pow(c, 2**(m - i - 1), p) # 
        m = i
        c = pow(b, 2, p)
        t = (t * c) % p
        r = (r * b) % p

    return r, p - r

